mapper.select:
select A.id, A.date, A.driverId, A.driverName, A.vehicleId, A.plateNum, A.routeName, A.dispatchTime, A.scheduleTime, A.departureTime, A.arrivalTime, FROM_UNIXTIME(UNIX_TIMESTAMP(A.departureTime),'%H:%i:%S') as departureString, FROM_UNIXTIME(UNIX_TIMESTAMP(A.scheduleTime),'%H:%i:%S') as scheduleString, FROM_UNIXTIME(UNIX_TIMESTAMP(A.arrivalTime),'%H:%i:%S') as arrivalString, A.status, A.starttag, A.siteName, A.late, A.trip, A.comsuTime, B.selfNum, A.spacing, A.mileage, A.reportSiteCount, A.isThroughUpMonitorSite, A.isThroughDownMonitorSite, A.whetherSwipeDeparture, A.hasNextWorkplan from dispatch.workplan A left join dispatch.vehicle B on A.vehicleId = B.vehicleId where A.`date`=#{queryWorkplan.date} and A.routeName=#{queryWorkplan.routeName}
and A.vehicleId=#{queryWorkplan.vehicleId}
and A.driverId=#{queryWorkplan.driverId}
order by A.scheduleTime
